import styled from '@emotion/styled';
import { Button, Container, Paper, Stack, Typography } from '@mui/material';
import React, { useState } from 'react';
import TextField from '@mui/material/TextField';

import { useNavigate } from 'react-router-dom';
import { fetchToken, setToken } from '../Auth';
import axios from 'axios';

import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

// Login form styles
const LoginWrap = styled(Paper)(({ theme }) => ({
    borderRadius: 20,
    width: '100%',
    maxWidth: 500,
    display: 'flex',
    alignItems: 'center',
    padding: 5,
    paddingLeft: 0,
    paddingRight: 0,
    justifyContent: 'center',
    flexDirection: 'column',
    flexWrap: 'nowrap',
}))

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

// Export the login UI 
export default function AddAccount() {

    // Get Password and Username
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [wallet, setwalletAddress] = useState('')
    const [profilePic, setprofilePic] = useState('')
    const [balance, setbalance] = useState()

    const [error_msg, setError_msg] = useState('')
    const [error, setError] = useState(false)

    // Forward in successfull
    const navigate = useNavigate();

    // Checking filled password and username
    // const login = (e) => {
    //     e.preventDefault();
    //     if ((username === "") || (password === "")) {
    //         setError_msg("Fill the username and password")
    //         setError(true)
    //     } else {
    //         axios
    //             .post("http://localhost:8000/login", {
    //                 username: username,
    //                 password: password,
    //             })
    //             .then((response) => {
    //                 if (response.data.token) {
    //                     setToken(response.data.token);
    //                     localStorage.setItem("username", username);
    //                     navigate("/profile");
    //                 } else {
    //                     setError_msg(response.data.error)
    //                 }
    //             });
    //     }
    // }

    const [showPassword, setShowPassword] = React.useState(false);

    return (
        <Container sx={{
            height: '80vh',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            marginTop: 4
        }} fixed>
            <LoginWrap elevation={24}>
                <Stack sx={{ width: '100%', maxWidth: '00px' }} mt={3}>
                    <Stack>
                        <Typography pl={1} fontSize={20} fontWeight={600}>Username</Typography>
                        <TextField
                            id="outlined-multiline-flexible"
                            multiline
                            maxRows={4}
                            sx={{
                                padding: 2
                            }}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </Stack>
                    <Typography pl={1} fontWeight={600}>Password</Typography>
                    <TextField
                        id="outlined-multiline-flexible"
                        multiline
                        maxRows={4}
                        sx={{
                            marginTop: 1,
                            marginBottom: 2,
                            padding: 2
                        }}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <Snackbar open={error} autoHideDuration={6000} >
                        <Alert severity="error" sx={{ width: '100%' }} >
                            <Typography>{error_msg}</Typography>
                        </Alert>
                    </Snackbar>
                    <Button sx={{ width: 'min-content', marginTop: 2, marginBottom: 6, marginLeft: 2 }} variant="contained" color='success' href="/profile">
                        {/* <Button onClick={login} sx={{ width: 'min-content', marginTop: 2, marginBottom: 6, marginLeft: 2 }} variant="outlined" href="/profile"> */}
                        Register
                    </Button>
                </Stack>
            </LoginWrap>
        </Container>
    )
}
