        } else {

        }
        OpenResultDialog()
    }
    return (
        <Container fixed>
            <Dialog
                fullWidth='true'
                maxWidth='md'
                open={Resulthandle}
                onClose={CloseResultDialog}
                scroll='body'
            >
                <DialogTitle>Search Results</DialogTitle>
                <DialogContent sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    m: 'auto',
                    maxWidth: '500px'
                }} >
                    <SearchResults keyword={keyword}/>
                </DialogContent>
                <DialogActions>
                    <Button onClick={CloseResultDialog}>Close</Button>
                </DialogActions>
            </Dialog>
            <HeaderContainer>
                <Logo />
                <Stack flexDirection="row">
                    <Search onSubmit={search}>
                        <SearchIconWrapper>
                            <SearchIcon />
                        </SearchIconWrapper>
                        <StyledInputBase
                            placeholder="Searchâ€¦"
                            inputProps={{ 'aria-label': 'search' }}
                            onChange={handle}
                        />
                    </Search>
                    <Link color='#000' href='/profile'>
                        <AccountCircleIcon fontSize='large' />
                    </Link>
                </Stack>
            </HeaderContainer>
        </Container>
    )
}
