import axios from 'axios'

import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import DialogContentText from '@mui/material/DialogContentText';
import FormControl from '@mui/material/FormControl';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { styled } from '@mui/material/styles';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import IconButton from '@mui/material/IconButton';
import { Typography, Stack, TextField, Container } from '@mui/material';
import PublishIcon from '@mui/icons-material/Publish';
import { useNavigate } from 'react-router-dom';

const VisuallyHiddenInput = styled('input')({
    clip: 'rect(0 0 0 0)',
    clipPath: 'inset(50%)',
    height: 1,
    overflow: 'hidden',
    position: 'absolute',
    bottom: 0,
    left: 0,
    whiteSpace: 'nowrap',
    width: 1,
});

export default function AssestUpload(props) {
    const [image, setImage] = useState(null)
    const [imagePreview, setImagePreview] = useState(null)
    const [assestName, setAssestName] = useState("Assest Name")
    const [price, setPrice] = useState('ETH')
    const [category, setCategory] = useState("Future")
    const username = localStorage.getItem("username")

    const [form, setform] = useState(second)

    const SelectAssest = (e) => {
        const file = e.target.files[0];
        setImage(file);
        console.log(image)

        // Create a URL for the uploaded image to display in the preview
        const reader = new FileReader();
        reader.onload = () => {
            setImagePreview(reader.result);
        };
        reader.readAsDataURL(file);
    };

    const upload = async () => {

        const formData = new FormData();
        formData.append('image', image);
        formData.append('name', assestName);
        formData.append('owner', username);
        formData.append('price', price);
        formData.append('category', category);

        axios
            .post('http://localhost:8000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            })
            .then(response => {
                // Handle the response from the FastAPI backend
                if (response.data.msg = "uploaded") {
                    console.log("sucess!")
                }else
                {
                    console.log(response.data.msg)
                }
            })
            .catch(error => {
                // Handle errors
                console.log("error")
            });

    }

    return (
        <Container>

            <IconButton sx={{ width: 'inherit', m: 'auto', mb: '20px', }} component="label">
                {!image ? (
                    <Stack sx={{
                        width: 'inherit',
                        maxWidth: '300pt',
                        height: '300pt',
                        display: 'flex',
                        borderStyle: 'dashed',
                        borderRadius: '24px',
                        justifyContent: 'center'
                    }} alignItems='center'>
                        <CloudUploadIcon fontSize='large' />
                        <Typography>Upload Your NFT</Typography>
                        <DialogContentText>
                            Keep using .jpg, .png formats
                        </DialogContentText>
                        <VisuallyHiddenInput onChange={SelectAssest} type="file" accept='.jpg,.jpeg,.png' />
                    </Stack>
                ) : (
                    <img width="100%" src={imagePreview} alt='Uploaded'></img>
                )}

            </IconButton>

            <Box
                noValidate
                component="form"
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    m: 'auto',
                    width: '100%',
                }}
            >
                <InputLabel>Name</InputLabel>
                <TextField
                    id="outlined-multiline-flexible"
                    multiline
                    maxRows={4}
                    sx={{
                        marginTop: 1,
                        marginBottom: 2,
                    }}
                    placeholder={assestName}
                    onChange={(e) => { setAssestName(e.target.value) }}
                />
                <InputLabel>Price</InputLabel>
                <TextField
                    id="outlined-multiline-flexible"
                    multiline
                    maxRows={4}
                    sx={{
                        marginTop: 1,
                        marginBottom: 2,
                    }}
                    placeholder={price}
                    onChange={(e) => setPrice(e.target.value)}
                />
                <InputLabel>Category</InputLabel>
                <Select
                    autoFocus
                    value={category}
                    onChange={(e) => { setCategory(e.target.value) }}
                >
                    <MenuItem value="gaming">Gaming</MenuItem>
                    <MenuItem value="future">Future</MenuItem>
                    <MenuItem value="art">Art</MenuItem>
                    <MenuItem value="photography">Photography</MenuItem>
                </Select>

                <FormControl sx={{ mt: 2, maxWidth: 120 }}>
                    <Button onClick={upload} component="label" variant="contained" startIcon={<PublishIcon />}>
                        Upload
                    </Button>

                </FormControl>
            </Box>
        </Container>
    );
}
