import axios from 'axios'

import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { styled } from '@mui/material/styles';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import IconButton from '@mui/material/IconButton';
import { Typography, Stack, TextField, Container } from '@mui/material';

const VisuallyHiddenInput = styled('input')({
    clip: 'rect(0 0 0 0)',
    clipPath: 'inset(50%)',
    height: 1,
    overflow: 'hidden',
    position: 'absolute',
    bottom: 0,
    left: 0,
    whiteSpace: 'nowrap',
    width: 1,
});

export default function AssestUpload(props) {
    // const [open, setOpen] = useState(props.handle);
    const [image, setImage] = useState(null)

    // const handleClose = () => {
    //     setOpen(false);
    // };


    return (
        <Container sx={{display: ''}} height="100vh">
            <Dialog
                fullWidth='true'
                maxWidth='md'
                open='true'

            >

                <DialogTitle>Upload Your NFT Here</DialogTitle>
                <DialogContent sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    m: 'auto'
                }}>
                    <IconButton sx={{ m: 'auto', mb: '20px', }} component="label">
                        <Stack sx={{
                            width: '300pt',
                            height: '300pt',
                            display: 'flex',
                            borderStyle: 'dashed',
                            borderRadius: '24px',
                            display: 'flex',
                            justifyContent: 'center'
                        }} alignItems='center'>
                            <CloudUploadIcon fontSize='large' />
                            <Typography>Upload Your NFT</Typography>
                            <DialogContentText>
                                Keep using .jpg, .png formats
                            </DialogContentText>
                            <VisuallyHiddenInput type="file" accept='.jpg,.jpeg,.png' />
                        </Stack>
                    </IconButton>

                    <Box
                        noValidate
                        component="form"
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            m: 'auto',
                            width: '100%',
                        }}
                    >
                        <InputLabel>Category</InputLabel>
                        <TextField
                            id="outlined-multiline-flexible"
                            multiline
                            maxRows={4}
                            sx={{
                                marginTop: 1,
                                marginBottom: 2,
                            }}
                        />
                        <InputLabel>Category</InputLabel>
                        <Select
                            autoFocus
                            value='xs'
                            label="maxWidth"
                            inputProps={{
                                name: 'max-width',
                                id: 'max-width',
                            }}
                        >
                            <MenuItem value="xs">Gaming</MenuItem>
                            <MenuItem value="sm">Future</MenuItem>
                            <MenuItem value="md">Art</MenuItem>
                            <MenuItem value="lg">Photography</MenuItem>
                        </Select>

                        <FormControl sx={{ mt: 2, minWidth: 120 }}>
                            <Button component="label" variant="contained" startIcon={<CloudUploadIcon />}>
                                Submit
                                <VisuallyHiddenInput type="file" accept='image/*' />
                            </Button>

                        </FormControl>
                    </Box>
                </DialogContent>
                {/* <DialogActions>
                    <Button onClick={handleClose}>Close</Button>
                </DialogActions> */}
            </Dialog>
        </Container>
    );
}
